mapper.select:
SELECT g.goods_id AS goodsId, g.goods_name AS goodsName, g.goods_subtitle AS goodsSubtitle, g.goods_image AS goodsImage, g.goods_description AS goodsDescription, g.store_id AS storeId, g.store_name AS storeName, g.spec_id AS specId , g.gc_id AS gcId, g.gc_big_id AS gcBigId, gs.spec_goods_price AS specGoodsPrice FROM shop_goods g INNER JOIN shop_goods_spec gs ON g.goods_id=gs.goods_id AND gs.spec_isopen=1 WHERE goods_show=1 and g.is_del=0
AND gc_id=#{gcId}
AND gc_big_id=#{gcId}
AND g.goods_name LIKE concat('%',#{goodsName},'%')
AND g.goods_id=#{goodsId}
GROUP BY gs.goods_id
ORDER BY g.salenum DESC, g.goods_store_price ASC
ORDER BY g.create_time DESC
ORDER BY g.salenum DESC
ORDER BY gs.spec_goods_price asc
ORDER BY gs.spec_goods_price desc
limit #{page},#{row}

mapper.update:
UPDATE shop_goods_spec SET
	spec_goods_storage=#{goodsStorage},
	spec_salenum =#{specSalenum}
WHERE goods_id=#{goodsId} AND goods_spec_id=#{specId}

