mapper.select:
SELECT ga.goods_spec_id AS goodsSpecId, ga.activity_price AS activityPrice, ga.activity_repertory AS activityRepertory, ga.discount_price AS discountPrice, ga.spec_price AS specPrice, ga.remark AS remark , ga.reserve_one_pay_price AS onePayPrice, ga.pledge_price AS pledgePrice, ga.reserve_two_pay_price AS twoPayPrice, ga.has_been AS hasBeen, ga.virtual, a.reserve_pay_type AS reservePayType, a.activity_start_time AS actStartTime, a.activity_end_time AS actEndTime , a.reserve_two_start_time AS towStartTime, a.reserve_two_end_time AS towEndTime , a.predict_time AS predictTime , a.reserve_flow AS reserveFlow , a.act_id AS actId , a.limit_number as limitNumber FROM ( SELECT limit_number,reserve_flow,predict_time,act_id,reserve_pay_type,activity_start_time,activity_end_time,reserve_two_start_time,reserve_two_end_time FROM shop_goods_activity WHERE act_state=2 AND activity_type IN (
#{o}
)) a INNER JOIN shop_goods_act_price ga ON a.act_id=ga.act_id AND goods_id=#{goodsId} AND is_start=1

