mapper.select:
select count(0)
from mc_coupon_receive
where member_id = #{memberid}
and coupon_id = #{couponId}
and DATE_FORMAT(date_time,'YYYY-mm-dd') = DATE_FORMAT(now(),'YYYY-mm-dd')

select DISTINCT
mc.*, sm.member_mobile as memberMobile, msi.id as storeId, msi.shop_name as shopName, msi.default_head as defaultHead, mcp.id as pictureId, mcp.picture_path as picturePath, mcr.id as receiveId, mcr.usage_state as usageState, mcr.date_time as dateTime mcr.award_code as awardCode
from mc_coupon mc inner join mc_coupon_picture mcp on mc.id = mcp.coupon_id inner join mc_coupon_receive mcr on mc.id = mcr.coupon_id inner join mc_store_information msi on mc.number_id = msi.mc_number_id inner join shop_member sm on mcr.member_id = sm.user_id
where mcp.picture_type = 1 and mc.to_examine = 2 and mcr.member_id = #{memberId}
and mcr.usage_state = #{usageState}
group by mc.id ORDER BY mcr.date_time desc
