mapper.select:
select store.*, management.management_name as storeTotalTypeName, management1.management_name as storeTypeName, sn.DISTRICT from mc_store_information store
left join mc_management_type management on management.management_id = store.store_total_type left join mc_management_type management1 on management1.management_id = store.store_son_type left join mc_shop_tag shoptag on store.id = shoptag.store_id left join sys_nation sn on sn.CODE = store.area_code
where store.store_total_type = #{storeTotalType} And store.to_examine = 2
and store.store_son_type = #{storeSonType}
and store.city_code = #{cityCode}
and store.area_code = #{areaCode}
and shoptag.tag_id = #{tagId}
and store.shop_name like concat('%' ,#{shopName}, '%')
and store.per_capita_price >= #{startingPrice} and store.per_capita_price <= #{closingPrice}
ORDER BY store.per_capita_price
desc
