mapper.select:
SELECT management_id, parent_id, management_name, sort, picture_colour, picture_code FROM mc_management_type WHERE 1=1
and management_name = #{managementName}
and parent_id = #{parentId}
and management_name != "百货购物"
and (parent_id is null OR parent_id = 0)

SELECT management.management_id, management.parent_id, management.management_name, management.sort, management.picture_colour, management.picture_code, management1.management_id as management_id_a, management1.parent_id as parent_id_a, management1.management_name as management_name_a, management1.sort as sort1, management1.picture_colour as picture_colour1, management1.picture_code as picture_code1 FROM mc_management_type management left join mc_management_type management1 on management1.parent_id = management.management_id WHERE 1=1 and management1.parent_id = management.management_id
and management.management_name = #{managementName}
and management.management_id = #{managementId}
